app.post('/api/cart/:transactionId/auto-check', authenticateToken, async (req, res) => {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');
    const { transactionId } = req.params;

    const tx = await client.query(
      'SELECT t.*, l.seller_id, l.id as listing_id FROM transactions t LEFT JOIN listings l ON t.listing_id = l.id WHERE t.id = $1 AND t.user_id = $2 FOR UPDATE',
      [transactionId, req.user.id]
    );

    if (tx.rows.length === 0) {
      await client.query('ROLLBACK');
      return res.status(404).json({ error: 'Transaction not found' });
    }

    const transaction = tx.rows[0];

    if (transaction.type !== 'purchase') {
      await client.query('ROLLBACK');
      return res.status(400).json({ error: 'Can only check purchases' });
    }

    if (transaction.refund_status) {
      await client.query('ROLLBACK');
      return res.status(400).json({ error: 'Refund already processed or requested' });
    }

    const purchaseTime = new Date(transaction.created_at).getTime();
    const now = Date.now();
    const fiveMinutes = 5 * 60 * 1000;

    if (now - purchaseTime > fiveMinutes) {
      await client.query('ROLLBACK');
      return res.status(400).json({ error: 'Check window expired (5 minutes limit)' });
    }

    await client.query('UPDATE users SET balance = balance + $1 WHERE id = $2', [transaction.amount, req.user.id]);
    if (transaction.seller_id) {
      await client.query('UPDATE users SET balance = balance - $1 WHERE id = $2', [transaction.amount, transaction.seller_id]);
    }

    await client.query('UPDATE transactions SET refund_status = $1 WHERE id = $2', ['auto_refunded', transactionId]);

    await client.query(
      'INSERT INTO transactions (user_id, listing_id, type, amount, status) VALUES ($1, $2, $3, $4, $5)',
      [req.user.id, transaction.listing_id, 'auto_refund', transaction.amount, 'confirmed']
    );

    await client.query('COMMIT');
    res.json({ message: 'Card check failed - Automatic refund processed', refunded: true });
  } catch (err) {
    await client.query('ROLLBACK');
    res.status(500).json({ error: 'Failed to process auto-check' });
  } finally {
    client.release();
  }
});

